            protected void setSourceFile( AbstractSyntaxTree input ) {	
                // Collect any source tables (there should only be a single one)
                def srcLst = input.getFromList().collect { it.getSourceTables().collect { it } }.flatten()
                if ( srcLst.size() <= 0 && srcLst.size() > 1 )
                	throw new OdiKMException("ODIKM-FILE-10015", "ODIKM-FILE-10015: Only a single file supported as source" )
                def sourceFile = srcLst[0]
                sourceFileDataStore = sourceFile.getModelTable().getBoundDataStore()
            }

            protected void setHiveSourceTableOrQuery( AbstractSyntaxTree input, MapPhysicalNode node ) {
                if ( useHiveStaging ) {
                    hiveQueryString = input.getText(node.getTemplateUtils())
                } else {
                    // Collect any source tables (there should only be a single one)
                    def srcLst = input.getFromList().collect { it.getSourceTables().collect { it } }.flatten()
                    if ( srcLst.size() <= 0 && srcLst.size() > 1 )
                    	throw new OdiKMException("ODIKM-FILE-10015", "ODIKM-HIVE-10015: Only a single table supported as source for USE_HIVE_STAGING=false" )
                    def sourceTable = srcLst[0]
                    sourceTableDataStore = sourceTable.getModelTable().getBoundDataStore()
                }
            }

            void defineSqlStagingTable() {
                // Create DDL for staging table
                def stagingTable = getStagingTable()
                stgTableAST = new SqlCreateTableStatement( "abc", "def", stagingTable, destTechno, ffFinder )
                def stgTableColumnList = physicalNode.getPhysicalColumns().collect { physCol -> return Column.getColumn( stagingTable, physCol) }               
                stgTableAST.setColumns( stgTableColumnList )                
                stgTableAST.tableOptions = getOption( "WORK_TABLE_OPTIONS" )
            }

