                def uniquenessTag   = physicalNode.getPhysicalDesign().isConcurrent() ? "%UNIQUE_STEP_TAG" : ""
                oracleLSchema       = physicalNode.getAPNodeTargetExUnit().getLogicalSchemaList().get(0).getName()
