                // Validate KM option value for OLH_OUTPUT_MODE
                if ( isExtTabRequired && option_EXT_TAB_DIR_LOCATION.equalsIgnoreCase( EXT_TAB_DIR_LOCATION_UNDEFINED ) )
                    throw new OdiKMException("ODIKM-STDKM-10000", "ODIKM-STDKM-10000: Invalid value ''{0}'' for option 'EXT_TAB_DIR_LOCATION'", [option_EXT_TAB_DIR_LOCATION] as String[] )

                // Validate some settings for OLH_OUTPUT_MODE = OSCH
                if ( isOutputModeOSCH ) {
                   if ( !isFromFile )
