                   if ( option_EXTRA_OLH_CONF_PROPERTIES.trim().length() > 0 )
                       throw new OdiKMException("ODIKM-HIVE-10010", "ODIKM-HIVE-10010: OLH_OUTPUT_MODE = OSCH does not support any EXTRA_OLH_CONF_PROPERTIES")
