import oracle.odi.domain.mapping.component.km.*
@GeneratorDelegateClass(componentType=DatastoreComponent.COMPONENT_TYPE_NAME, descriptionKey="GeneratorDelegateClass.OracleExternalTableCompKM")
public class XKM_Oracle_External_Table extends SQLDatastoreCompKM {

  protected escapeChars;
  protected rawExpr;

  private mBinding = null;
  public void setBinding(Binding binding) {
    super.setBinding(binding);
    mBinding = binding;
    Map variables = binding.getVariables();

    escapeChars  = { rawTxt -> 
        return rawTxt.replaceAll("\\:","\\\\:");
    }

    rawExpr = { optVal ->
        if (optVal != null) { 
            return optVal.replaceAll("\\/\\%\\%","").replaceAll("\\%\\%\\/",""); 
        }
        return null;
    }

    mBinding.setVariable("escapeChars", escapeChars);
    mBinding.setVariable("rawExpr", rawExpr);
  }

  public XKM_Oracle_External_Table(){
  }

}

odiRuntimeAccessName = OdiRef.getOdiGeneratedAccessName("TARG_NAME", physicalNode, "A");
tableAlias = component.getAlias();
tgtColList = { 
    def tgtTab = (IDataStore) component.getBoundObject()
    def cols = tgtTab.getColumns();
    String result = ""
    def first = true
    for (col in cols) {
        if (!first) result += ",\n"
        result += col.getName() + " "
        if (col.getDdlDataType().contains("NUMBER(50)") ) {
            result += "NUMBER(38)"  ;
        } else {
            result += col.getDdlDataType() ;
        }
        first = false
   }
   return result
}

